import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router';

const SigleHotelInfo = () => {
    const {id} = useParams();
    const [hotelInfo, setHotelInfo] = useState({});

    useEffect(() => {
        fetch(`http://localhost:5000/singleHotel/${id}`)
        .then(res => res.json())
        .then(data => setHotelInfo(data[0]))
    }, [id])
    return (
        <div>
            <div className='d-flex' style={{widht}}>
            <img src={hotelInfo.banner1} alt="" />
            <img src={hotelInfo.banner2} alt="" />
            </div>
        </div>
    );
};

export default SigleHotelInfo;
